-- Generated by the database client.
CREATE TABLE certification_schemes(
    certification_scheme_id SERIAL NOT NULL,
    certification_scheme_name varchar(255) NOT NULL,
    framework framework_enum,
    certificate_type certificate_type_enum,
    geographic_coverage geographic_coverage_enum,
    accreditation_body_id integer,
    issuing_body_id integer,
    address_id integer,
    overview jsonb,
    validity varchar,
    rfnbo_check rfnbo_check,
    lifecycle_coverage lifecycle_coverage_enum,
    comparator_value varchar(255),
    ghg_reduction numeric,
    pcf_calculation_methodology varchar(255),
    pcf_approach pcf_approach_enum,
    chain_of_custody_model chain_of_custody_model_enum,
    traceability_method traceability_method_enum,
    blend_ratio real,
    coverage text,
    certification_details jsonb,
    PRIMARY KEY(certification_scheme_id),
    CONSTRAINT fk_accreditation_body FOREIGN key(accreditation_body_id) REFERENCES accreditation_bodies(ab_id),
    CONSTRAINT fk_address FOREIGN key(address_id) REFERENCES address(address_id),
    CONSTRAINT fk_issuing_body FOREIGN key(issuing_body_id) REFERENCES issuing_bodies(ib_id),
    CONSTRAINT check_json_keys CHECK ((overview ? 'overview'::text) AND (overview ? 'requirements'::text) AND (overview ? 'process'::text) AND (overview ? 'short_certification_overview'::text) AND (overview ? 'recommendation_overview'::text) AND (overview ? 'track_process'::text)),
    CONSTRAINT check_json_types CHECK (...),
    CONSTRAINT check_certification_details_all_fields CHECK (...)
);
COMMENT ON COLUMN certification_schemes.blend_ratio IS 'comment';
COMMENT ON COLUMN certification_schemes.coverage IS 'Geographic coverage: EU for regional, Global for global';

